mapper.select:
SELECT
    id, name, luggage_id, reg_date
FROM xray_images

    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}
    AND type = 'R'

LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(sub.bcount) AS count FROM(SELECT count(*) as bcount
FROM xray_images

    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}

GROUP BY luggage_id HAVING luggage_id IS NOT NULL) sub;

SELECT A.id as id, A.device_id as device_id, if(A.power,'true','false') as status, B.name as device_name, A.reg_date as reg_date FROM device_power_state_log A JOIN device B ON A.device_id = B.id
AND B.type = 'X' AND A.power = true AND A.power = false AND B.name LIKE CONCAT('%', #{searchText}, '%') AND A.reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND A.reg_date >= #{dateStart} AND A.reg_date <= #{dateEnd}
ORDER BY A.reg_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT A.id as id, A.device_id as device_id, if(A.power,'true','false') as status, B.name as device_name, A.reg_date as reg_date FROM device_power_state_log A JOIN device B ON A.device_id = B.id
AND B.type = 'C' AND A.power = true AND A.power = false AND B.name LIKE CONCAT('%', #{searchText}, '%') AND A.reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND A.reg_date >= #{dateStart} AND A.reg_date <= #{dateEnd}
ORDER BY A.reg_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT user_id, ip, reg_date
FROM login_history

   AND user_id LIKE CONCAT('%', #{searchText}, '%')
    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}

ORDER BY reg_date DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*) FROM login_history

    AND user_id LIKE CONCAT('%', #{searchText}, '%')
    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}

SELECT user_id, main_menu, sub_menu, type, reg_date FROM action_history
AND user_id LIKE CONCAT('%', #{searchText}, '%') AND main_menu LIKE CONCAT('%', #{searchText}, '%') AND sub_menu LIKE CONCAT('%', #{searchText}, '%') AND type LIKE CONCAT('%', #{searchText}, '%') AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND reg_date >= #{dateStart} AND reg_date <= #{dateEnd}
ORDER BY reg_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT (SELECT name FROM users WHERE id = A.user_id) AS user_name, (SELECT name FROM device WHERE id = A.xray_id) AS xray_name, (SELECT name FROM device WHERE id = A.check_id) AS check_name, (SELECT passenger_code FROM passenger WHERE id = A.passenger_id) AS passenger_code, reg_date, CASE WHEN type = 'P' THEN '통과' WHEN type = 'C' THEN '개봉' END AS type
AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND reg_date >= #{dateStart} AND reg_date <= #{dateEnd}
from luggage A ORDER BY reg_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*) from luggage;

    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}
