mapper.select:
SELECT (select level2 from ai_detection where label_id = A.detection_id ) as labelName, detection_id, count(*) AS amount, score, round(100. * count(*) / sum(count(*)) over (), 2) AS percentage FROM xray_analyze_statistics A
AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND reg_date >= #{dateStart} AND reg_date <= #{dateEnd}
GROUP BY detection_id ORDER BY percentage DESC;

SELECT (select level2 from ai_detection where label_id = A.detection_id ) as labelName, detection_id, count(*) AS amount, score, round(100. * count(*) / sum(count(*)) over (), 2) AS percentage FROM xray_analyze_statistics A
AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 AND reg_date >= #{dateStart} AND reg_date <= #{dateEnd}
GROUP BY detection_id ORDER BY percentage DESC limit 10;

SELECT count(*) FROM xray_analyze_statistics

    AND reg_date between date(#{dateStart}) and date(#{dateEnd})+1 
    AND reg_date >= #{dateStart}
    AND reg_date <= #{dateEnd}
